import React, { useState, useEffect } from "react";
import { Search, Play, Calendar, Film, Star, Loader2, X } from "lucide-react";

const API_URL = "http://www.omdbapi.com/?i=tt3896198&apikey=c5f730ae";

const MovieCard = ({ movie, onClick }) => {
  const [imageError, setImageError] = useState(false);
  const [imageLoading, setImageLoading] = useState(true);

  const handleImageLoad = () => {
    setImageLoading(false);
  };

  const handleImageError = () => {
    setImageError(true);
    setImageLoading(false);
  };

  return (
    <div
      className="group relative bg-gray-800/50 backdrop-blur-sm rounded-xl overflow-hidden border border-gray-700/50 hover:border-orange-400/50 transition-all duration-300 transform hover:scale-105 hover:shadow-2xl hover:shadow-orange-500/20 cursor-pointer"
      onClick={() => onClick(movie)}
    >
      {/* Image Container */}
      <div className="relative aspect-[2/3] overflow-hidden">
        {imageLoading && (
          <div className="absolute inset-0 flex items-center justify-center bg-gray-800">
            <Loader2 className="w-8 h-8 animate-spin text-orange-400" />
          </div>
        )}
        <img
          src={
            movie.Poster !== "N/A" && !imageError
              ? movie.Poster
              : "https://via.placeholder.com/400x600/374151/f97316?text=No+Image"
          }
          alt={movie.Title}
          onLoad={handleImageLoad}
          onError={handleImageError}
          className={`w-full h-full object-cover transition-all duration-300 group-hover:scale-110 ${
            imageLoading ? "opacity-0" : "opacity-100"
          }`}
        />

        {/* Overlay on hover */}
        <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300">
          <div className="absolute top-4 right-4">
            <div className="bg-orange-500 text-white px-2 py-1 rounded-full text-xs font-semibold">
              {movie.Year}
            </div>
          </div>
          <div className="absolute bottom-4 left-4 right-4">
            <div className="flex items-center gap-2 mb-2">
              <Play className="w-4 h-4 text-orange-400" />
              <span className="text-orange-400 text-sm font-medium">
                Click to view details
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="p-4">
        <div className="flex items-center gap-2 mb-2">
          <Film className="w-4 h-4 text-orange-400" />
          <span className="text-gray-400 text-sm uppercase tracking-wide font-medium">
            {movie.Type}
          </span>
        </div>
        <h3 className="text-white font-bold text-lg leading-tight line-clamp-2 group-hover:text-orange-400 transition-colors duration-300">
          {movie.Title}
        </h3>
        <div className="flex items-center gap-2 mt-2">
          <Calendar className="w-4 h-4 text-gray-500" />
          <span className="text-gray-500 text-sm">{movie.Year}</span>
        </div>
      </div>
    </div>
  );
};

