                  <div>
                    <span className="text-gray-500">Title:</span> {movie.Title}
                  </div>
                  <div>
                    <span className="text-gray-500">Year:</span> {movie.Year}
                  </div>
                  <div>
                    <span className="text-gray-500">Type:</span> {movie.Type}
