import React, { useState, useEffect } from "react";

const API_URL = "http://www.omdbapi.com/?i=tt3896198&apikey=c5f730ae";

// Custom CSS Icons
const SearchIcon = () => (
  <svg
    width="20"
    height="20"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
  >
    <circle cx="11" cy="11" r="8"></circle>
    <path d="21 21l-4.35-4.35"></path>
  </svg>
);

const PlayIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
    <polygon points="5,3 19,12 5,21"></polygon>
  </svg>
);

const CalendarIcon = () => (
  <svg
    width="16"
    height="16"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
  >
    <rect x="3" y="4" width="18" height="18" rx="2" ry="2"></rect>
    <line x1="16" y1="2" x2="16" y2="6"></line>
    <line x1="8" y1="2" x2="8" y2="6"></line>
    <line x1="3" y1="10" x2="21" y2="10"></line>
  </svg>
);

const FilmIcon = () => (
  <svg
    width="16"
    height="16"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
  >
    <rect x="2" y="3" width="20" height="14" rx="2" ry="2"></rect>
    <line x1="8" y1="21" x2="16" y2="21"></line>
    <line x1="12" y1="17" x2="12" y2="21"></line>
  </svg>
);

const CloseIcon = () => (
  <svg
    width="20"
    height="20"
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="2"
  >
    <line x1="18" y1="6" x2="6" y2="18"></line>
    <line x1="6" y1="6" x2="18" y2="18"></line>
  </svg>
);

const LoaderIcon = () => (
  <div className="loader-spinner">
    <svg
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
    >
      <path d="M21 12a9 9 0 11-6.219-8.56" />
    </svg>
  </div>
);

const MovieCard = ({ movie, onClick }) => {
  const [imageError, setImageError] = useState(false);
  const [imageLoading, setImageLoading] = useState(true);

  const handleImageLoad = () => {
    setImageLoading(false);
  };

  const handleImageError = () => {
    setImageError(true);
    setImageLoading(false);
  };

  return (
    <div className="movie-card" onClick={() => onClick(movie)}>
      {/* Image Container */}
      <div className="movie-image-container">
        {imageLoading && (
          <div className="image-loader">
            <LoaderIcon />
          </div>
        )}
        <img
          src={
            movie.Poster !== "N/A" && !imageError
              ? movie.Poster
              : "https://via.placeholder.com/400x600/374151/f97316?text=No+Image"
          }
          alt={movie.Title}
          onLoad={handleImageLoad}
          onError={handleImageError}
          className={`movie-image ${imageLoading ? "loading" : ""}`}
        />

        {/* Overlay on hover */}
        <div className="movie-overlay">
          <div className="movie-year-badge">{movie.Year}</div>
          <div className="movie-play-info">
            <PlayIcon />
            <span>Click to view details</span>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="movie-content">
        <div className="movie-type">
          <FilmIcon />
          <span>{movie.Type}</span>
        </div>
        <h3 className="movie-title">{movie.Title}</h3>
        <div className="movie-year">
          <CalendarIcon />
          <span>{movie.Year}</span>
        </div>
      </div>
    </div>
  );
};

const MovieModal = ({ movie, isOpen, onClose }) => {
  if (!isOpen || !movie) return null;

  return (
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>
          <CloseIcon />
        </button>

        <div className="modal-image">
          <img
            src={
              movie.Poster !== "N/A"
                ? movie.Poster
                : "https://via.placeholder.com/800x450/374151/f97316?text=No+Image"
            }
            alt={movie.Title}
          />
        </div>

        <div className="modal-info">
          <div className="modal-header">
            <h2>{movie.Title}</h2>
            <div className="modal-meta">
              <div className="meta-item">
                <CalendarIcon />
                <span>{movie.Year}</span>
              </div>
              <div className="meta-item">
                <FilmIcon />
                <span>{movie.Type}</span>
              </div>
            </div>
          </div>

          <div className="modal-details">
            <p>
              This {movie.Type} was released in {movie.Year}. Click the close
              button or press ESC to return to the search results.
            </p>
            <div className="details-grid">
              <h3>Movie Details</h3>
              <div className="details-items">
                <div>
                  <span className="label">Title:</span> {movie.Title}
                </div>
                <div>
                  <span className="label">Year:</span> {movie.Year}
                </div>
                <div>
                  <span className="label">Type:</span> {movie.Type}
                </div>
                <div>
                  <span className="label">IMDB ID:</span> {movie.imdbID}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const searchMovies = async (title) => {
    if (!title.trim()) return;

    setLoading(true);
    setError("");

    try {
      const response = await fetch(`${API_URL}&s=${title}`);
      const data = await response.json();

      if (data.Response === "True") {
        setMovies(data.Search);
      } else {
        setMovies([]);
        setError(data.Error || "No movies found");
      }
    } catch (err) {
      setError("Failed to fetch movies. Please try again.");
      setMovies([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    searchMovies(searchTerm);
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      searchMovies(searchTerm);
    }
  };

  const openModal = (movie) => {
    setSelectedMovie(movie);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedMovie(null);
  };

  useEffect(() => {
    searchMovies("Superman");
  }, []);

  useEffect(() => {
    const handleEsc = (e) => {
      if (e.key === "Escape") {
        closeModal();
      }
    };

    if (isModalOpen) {
      document.addEventListener("keydown", handleEsc);
      document.body.style.overflow = "hidden";
    }

    return () => {
      document.removeEventListener("keydown", handleEsc);
      document.body.style.overflow = "unset";
    };
  }, [isModalOpen]);

  return (
    <div className="app-container">
      <div className="app-content">
        {/* Header */}
        <div className="app-header">
          <h1 className="app-title">MovieLand</h1>
          <p className="app-subtitle">Discover your next favorite movie</p>
        </div>

        {/* Search */}
        <div className="search-container">
          <div className="search-wrapper">
            <input
              type="text"
              placeholder="Search for movies, series, episodes..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={handleKeyPress}
              className="search-input"
            />
            <button
              onClick={handleSearch}
              disabled={loading}
              className="search-button"
            >
              {loading ? <LoaderIcon /> : <SearchIcon />}
            </button>
          </div>
        </div>

        {/* Loading State */}
        {loading && (
          <div className="loading-state">
            <LoaderIcon />
            <p>Searching for movies...</p>
          </div>
        )}

        {/* Error State */}
        {error && !loading && (
          <div className="error-state">
            <div className="error-content">
              <FilmIcon />
              <h2>Oops!</h2>
              <p>{error}</p>
            </div>
          </div>
        )}

        {/* Movies Grid */}
        {movies?.length > 0 && !loading && (
          <div className="movies-grid">
            {movies.map((movie) => (
              <MovieCard key={movie.imdbID} movie={movie} onClick={openModal} />
            ))}
          </div>
        )}

        {/* Empty State */}
        {movies?.length === 0 && !loading && !error && searchTerm && (
          <div className="empty-state">
            <FilmIcon />
            <h2>No movies found</h2>
            <p>Try searching with different keywords</p>
          </div>
        )}

        {/* Movie Modal */}
        <MovieModal
          movie={selectedMovie}
          isOpen={isModalOpen}
          onClose={closeModal}
        />
      </div>

